/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import React ,{ Component } from 'react'
import {Map, Marker, NavigationControl, InfoWindow, MapTypeControl, ScaleControl, OverviewMapControl} from 'react-bmap'

const markerStyle = {
  minWidth:'100px',height: '40px', lineHeight: '20px', background: '#009966', color:'#ffffff',borderRadius:'40px',
  boxShadow:"0px 0px 5px 1px rgba(0,204,102,1)",textAlign:"left",padding: "0 20px 0 20px",
  cursor:"pointer",
  "&:hover":{opacity:"0.8"}
}
class RbMap extends Component{
  //属性
  //mapStyle={{style: 'midnight'}} 样式配置
  //events={this.getEvents()} // 为地图添加各类监听事件
  render(){
    const BMap = window.BMap
    return(
      <Map
        center={{lng: 114.0661345267, lat: 22.5485544122}}
        zoom="11"
        style={{height:"100%"}}
        enableScrollWheelZoom={true}

      >
          <Marker position={{lng: 114.1671345267, lat: 22.5485544122}} />
          <InfoWindow position={{lng: 114.0661345267, lat: 22.5485544122}} text="内容" title="标题"/>
          <NavigationControl offset={new BMap.Size(10,60)} />
          <MapTypeControl />
          <ScaleControl />
          <OverviewMapControl />

          <Marker position={{lng: 114.2671345267, lat: 22.6485544122}} offset={new BMap.Size(-75, -60)}>
              <div
                onClick={function(){alert(1)}}
                css={markerStyle}>
                  阿斯顿发发生

              </div>
          </Marker>
      </Map>
    )
  }
}

export default RbMap
