      const script = document.createElement('script')
      script.type = 'text/javascript'
      script.dataset.name = 'map'
      script.src = `http://api.map.baidu.com/api?v=3.0&ak=${ak}&callback=init` //callback调用init函数。
      document.head.appendChild(script)
      window.init = ()=>{
        console.log(BMap)
        resolve(BMap)
      }
    })
  }
  componentDidMount(){
    this.MP(this.ak).then(BMap=>{
      console.log(BMap)
    })


  }

  getEvents = ()=>{
        return {
            click: (e) => {
                console.log('map click event', e.type);
            }
        }
    }


  render(){
    return (
      <div className='mapContainer'>

      </div>
    )
  }
}


export default WholeView
