/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import styled from '@emotion/styled'
import React, { Component } from 'react'
import $ from 'jquery'

import BdMap from '../components/BdMap'


class WholeView extends Component{
  constructor(props){
    super(props)
  }
  state = {
    mapW: ''
  }

  componentDidMount(){
    const formap = document.querySelector(".formap")
    //const windowH = parseInt(document.body.clientHeight,0)
    // formapH = windowH +'px'
    let formapH = window.getComputedStyle(formap).height || formap.style.height

    window.onresize = ()=>{
      formapH = window.getComputedStyle(formap).height || formap.style.height
    }
  }


  render(){
    return (
      <div className="formap">
        <BdMap
          style={{
            width:"100%",
            height:"100%"
          }}

        />
      </div>
    )
  }
}


export default WholeView
