class Configure extends Component {
  constructor(...args) {
    super(...args);
    this.state = {
      message: '',
      //
      messageForSelectModel: '',
      //
      pattern: '',
      //
      result: '',
      //
      lastOne: {
        //
        message: '',
        pattern: '',
        result: ''
      },
      xData: [],
      yData: [],
      pageSize: 10,
      isSpining: false,
      activeKey: [],
      curTime: [],
      //indexList:[],
      compile_status: 'all',
      isSpining: false,
      isSelectModel: false,
      //
      isExistModel: false,
      //
      selArr: [],
      //
      wholeArr: [],
      //
      curSnippet: {
        //
        id: '',
        start: '',
        end: '',
        str: '',
        name: '',
        selected: ''
      },
      visible: false,
      modalVisible: false,
      rulename: '',
      //
      ruledes: '',
      //
      appname: '',
      appNameList: [],
      drawerVisible: false,
      group_app: ['all_group'],
      groupAppList: [],
      currule: '',
      //
      ruleList: [],
      //
      popVisible: false,
      condition: true
    };

    this.getCurRuleList = appname => {
      wyAxiosPost('Rule/getRuleList', {
        appname
      }, result => {
        const responseData = result.data.msg;

        if (this._isMounted && responseData.length > 0) {
          this.setState({
            ruleList: responseData
          });
        }
      });
    };

    this.getGoupAppList = () => {
      wyAxiosPost('Group/getGroupApp', {}, result => {
        const responseData = result.data.msg;

        if (this._isMounted) {
          this.setState({
            groupAppList: responseData
          });
        }
      });
    };

    this.compile_statusChange = value => {
      this.setState({
        compile_status: value
      }, () => {
        this.getLog();
      });
    };

    this.isSelectModelChange = e => {
      if (this._isMounted) {
        this.setState({
          isSelectModel: e.target.checked,
          isExistModel: false,
          messageForSelectModel: e.target.checked ? this.state.message : '',
          selArr: [],
          wholeArr: [],
          currule: '',
          pattern: '',
          result: ''
        });
      }
    };

    this.selectWord = event => {
      const e = window.event || event;
      e.preventDefault();
      e.stopPropagation();
      const curMessage = this.state.message;
      const start = e.target.selectionStart;
      const end = e.target.selectionEnd; //

      if (this.isIntersect(this.state.selArr, start, end)) {
        _message.warning(',');

        return;
      }

      const selectedText = curMessage.slice(start, end);

      const notrim = _.trim(selectedText);

      const randomCount = Math.random();
      const curTime = new Date().getTime();
      const asId = parseInt(randomCount, 10) + parseInt(curTime, 10) * 100;
      const id = asId.toString();

      if (selectedText.length > 0 && notrim.length === 0 || selectedText.length === 0 || selectedText === '') {
        _message.warning('');

        return;
      }

      if (selectedText === this.state.message) {
        return;
      }

      if (this._isMounted) {
        this.setState({
          curSnippet: {
            id,
            start,
            end,
            str: selectedText,
            name: '',
            selected: true
          }
        }, () => {
          this.showModal();
        });
      }
    };

    this.getAppName = () => {
      wyAxiosPost('Source/getAppName', {}, result => {
        const responseData = result.data.msg;

        if (this._isMounted) {
          this.setState({
            appNameList: responseData
          });
        }
      });
    };

    this.appnameChange = value => {
      if (this._isMounted) {
        this.setState({
          appname: value
        });
      }
    };

    this.compileArr = (arr, str) => {
      const resolveArr = [];

      if (arr && arr.length > 0) {
        arr.map((item, index) => {
          if (index === arr.length - 1) {
            if (index === 0 && item.start !== 0) {
              const noSelect = {}; // const randomCount = Math.random();
              // const curTime = new Date().getTime();
              // const asId = parseInt(randomCount,10) + parseInt(curTime,10)*100

              const id = arr[index].id - 1;
              noSelect.id = id;
              noSelect.selected = false;
              noSelect.start = 0;
              noSelect.end = arr[index].start;
              noSelect.str = str.slice(noSelect.start, noSelect.end);
              resolveArr.push(noSelect);
            }

            resolveArr.push(item);
            const noSelect = {}; // const randomCount = Math.random();
            // const curTime = new Date().getTime();
            // const asId = parseInt(randomCount,10) + parseInt(curTime,10)*100

            const id = arr[index].id + 1;
            noSelect.id = id;
            noSelect.selected = false;
            noSelect.start = item.end;
            noSelect.end = str.length;
            noSelect.str = str.slice(noSelect.start, noSelect.end);
            resolveArr.push(noSelect);
          } else {
            if (index === 0 && item.start !== 0) {
              const noSelect = {}; // const randomCount = Math.random();
              // const curTime = new Date().getTime();
              // const asId = parseInt(randomCount,10) + parseInt(curTime,10)*100

              const id = arr[index].id - 1;
              noSelect.id = id;
              noSelect.selected = false;
              noSelect.start = 0;
              noSelect.end = arr[index].start;
              noSelect.str = str.slice(noSelect.start, noSelect.end);
              resolveArr.push(noSelect);
            }

            resolveArr.push(item);
            const noSelect = {}; // const randomCount = Math.random();
            // const curTime = new Date().getTime();
            // const asId = parseInt(randomCount,10) + parseInt(curTime,10)*100

            const id = arr[index].id + 1;
            noSelect.id = id;
            noSelect.selected = false;
            noSelect.start = item.end;
            noSelect.end = arr[index + 1].start;
            noSelect.str = str.slice(noSelect.start, noSelect.end);
            resolveArr.push(noSelect);
          }
        });
      }

      return resolveArr;
    };

    this.isIntersect = (arr, start, end) => {
      let isIn = false;

      if (arr && arr.length > 0) {
        isIn = arr.some(item => {
          const istart = item.start;
          const iend = item.end;
          return (istart < start || istart == start) && start < iend || istart < end && (end < iend || end == iend);
        });
      }

      return isIn;
    };

    this.showModal = () => {
      this.setState({
        visible: true
      });
    };

    this.handleOk = e => {
      if (this.state.curSnippet.name === '') {
        _message.warning('');
      } else {
        const selArr = _.cloneDeep(this.state.selArr);

        const isRename = selArr.some(item => {
          return item.name === e.target.value;
        });

        if (isRename) {
          _message.warning('');
        } else {
          const curArr = _.cloneDeep(this.state.selArr);

          const newArr = _.cloneDeep(this.state.curSnippet);

          curArr.push(newArr); //

          const haha = _.sortBy(curArr, item => {
            return item.start;
          });

          if (this._isMounted) {
            this.setState({
              selArr: _.cloneDeep(haha)
            }, () => {
              const wholeArr = this.compileArr(this.state.selArr, this.state.message);

              if (this._isMounted) {
                this.setState({
                  wholeArr
                }, () => {
                  this.handleCancel();
                  const reg = decodeArr(this.state.wholeArr, this.state.messageForSelectModel);

                  if (this._isMounted) {
                    this.setState({
                      pattern: reg
                    });
                  }
                });
              }
            });
          }
        }
      }
    };

    this.handleCancel = e => {
      if (this._isMounted) {
        this.setState({
          visible: false,
          curSnippet: {
            id: '',
            start: '',
            end: '',
            str: '',
            name: ''
          }
        });
      }
    };

    this.modalCancel = e => {
      if (this._isMounted) {
        this.setState({
          modalVisible: false,
          selArr: [],
          //
          wholeArr: [],
          //
          curSnippet: {
            id: '',
            start: '',
            end: '',
            str: '',
            name: '',
            selected: ''
          },
          rulename: '',
          appname: '',
          ruledes: '',
          lastOne: {
            message: '',
            pattern: '',
            result: ''
          },
          message: '',
          pattern: '',
          result: '',
          drawerVisible: false
        });
      }
    };

    this.partCancel = () => {
      if (this._isMounted) {
        this.setState({
          modalVisible: false,
          rulename: '',
          appname: '',
          ruledes: '',
          result: ''
        });
      }
    };

    this.modalShow = e => {
      if (this._isMounted) {
        this.setState({
          modalVisible: true
        });
      }
    };

    this.curSnippetChange = e => {
      const cur = _.cloneDeep(this.state.curSnippet);

      const compileCur = Object.assign({}, this.state.curSnippet, {
        name: e.target.value
      });

      if (this._isMounted) {
        this.setState({
          curSnippet: _.cloneDeep(compileCur)
        });
      }
    };

    this.clearData = () => {
      if (this._isMounted) {
        this.setState({
          selArr: [],
          //
          wholeArr: [],
          //
          curSnippet: {
