/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import styled from '@emotion/styled'
import React, { Component } from 'react'
import $ from 'jquery'
import { connect } from 'react-redux'
import { Input, TreeSelect } from 'antd'


import RbMap from '../components/RbMap'
const { Search } = Input
const { SHOW_PARENT } = TreeSelect
class WholeView extends Component{
  constructor(props){
    super(props)
  }
  state = {
    industryValue:[],
    treeData: [
                                  {
                                    title: 'Node1',
                                    value: '0-0',
                                    key: '0-0',
                                    children: [
                                      {
                                        title: 'Child Node1',
                                        value: '0-0-0',
                                        key: '0-0-0',
                                      },
                                    ],
                                  },
                                  {
                                    title: 'Node2',
                                    value: '0-1',
                                    key: '0-1',
                                    children: [
                                      {
                                        title: 'Child Node3',
                                        value: '0-1-0',
                                        key: '0-1-0',
                                      },
                                      {
                                        title: 'Child Node4',
                                        value: '0-1-1',
                                        key: '0-1-1',
                                      },
                                      {
                                        title: 'Child Node5',
                                        value: '0-1-2',
                                        key: '0-1-2',
                                      },
                                    ],
                                  },
                                ]

  }

  componentDidMount(){
    this._isMounted = true
  }
  industryValueChange = (value)=>{
    if(this._isMounted){
      this.setState({ industryValue:value });
    }
  }

  render(){
    //window动态高度
    //数选择框的属性
    const tProps = {
      treeData:this.state.treeData,
      value: this.state.industryValue,
      onChange: this.industryValueChange,
      treeCheckable: true,
      showCheckedStrategy: SHOW_PARENT,
      searchPlaceholder: '默认全部产业',
      multiple: true,
      allowClear: true,
      treeNodeFilterProp: 'title',
      maxTagCount: 0,
      maxTagPlaceholder:'haha',
      style: {
        width: 300,
        overflow: "hidden",
        textOverflow: "ellipsis",
        whiteSpace: "nowrap"
      },
    }
    const relH = this.props.windowH - 100
    return (
      <div className="formap" css={{position: "relative"}}>
        <div style={{height: `${relH}px`}}>
          <RbMap

          />
        </div>
        <div css={{width:"100%",height:"50px",lineHeight:"50px",position: "absolute",background:"#ffffff",top:"0px",padding: "0 20px 0 20px",background:"rgba(25,33,57,0.8)"}}>
          <Search
            placeholder="input search text"
            onSearch={value => console.log(value)}
            style={{ width: 200 }}
          />
          <TreeSelect {...tProps} />


        </div>
      </div>
    )
  }
}
const mapStateToProps = (state)=>({
  windowH: state.windowH.windowH
})

export default connect(mapStateToProps,null)(WholeView)
