/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import styled from '@emotion/styled'
import React, { Component } from 'react'
import $ from 'jquery'

import BdMap from '../components/BdMap'


class WholeView extends Component{
  constructor(props){
    super(props)
  }
  state = {
    mapW: ''
  }

  componentDidMount(){
    const formap = document.querySelector(".formap")
    const windowH = parseInt(document.body.clientHeight,0)
    formap.style.height = windowH -100 +'px'
    window.onresize = ()=>{
      const rwindowH = parseInt(document.body.clientHeight,0)
      formap.style.height = rwindowH - 100 +'px'
      console.log(formap.style.height)
    }
  }


  render(){
    return (
      <div className="formap">
        <BdMap

        />
      </div>
    )
  }
}


export default WholeView
