/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import styled from '@emotion/styled'
import React, { Component } from 'react'
import $ from 'jquery'
import { connect } from 'react-redux'
import { Input, TreeSelect } from 'antd'


import RbMap from '../components/RbMap'
const { Search } = Input
const { SHOW_PARENT, TreeNode } = TreeSelect
class WholeView extends Component{
  constructor(props){
    super(props)
  }
  state = {
    industryValue:[],
    treeData: [
                  {
                    title: 'Node1',
                    value: '0-0',
                    key: '0-0',
                    children: [
                      {
                        title: 'Child Node1',
                        value: '0-0-0',
                        key: '0-0-0',
                        children:[
                          {
                            title: '第三级',
                            value: '0-0-0-0',
                            key: '0-0-0-0',
                          },
                          {
                            title: '222',
                            value: '0-0-0-1',
                            key: '0-0-0-1',
                          }
                        ]
                      },
                    ],
                  },
                  {
                    title: 'Node2',
                    value: '0-1',
                    key: '0-1',
                    children: [
                      {
                        title: 'Child Node3',
                        value: '0-1-0',
                        key: '0-1-0',
                      },
                      {
                        title: 'Child Node4',
                        value: '0-1-1',
                        key: '0-1-1',
                      },
                      {
                        title: 'Child Node5',
                        value: '0-1-2',
                        key: '0-1-2',
                      },
                    ],
                  },
                ],
    cityValue:'',
    cityData: [
                  {
                    title: 'Node1',
                    value: '0-0',
                    key: '0-0',
                    children: [
                      {
                        title: 'Child Node1',
                        value: '0-0-1',
                        key: '0-0-1',
                      },
                      {
                        title: 'Child Node2',
                        value: '0-0-2',
                        key: '0-0-2',
                      },
                    ],
                  },
                  {
                    title: 'Node2',
                    value: '0-1',
                    key: '0-1',
                  },
                ],
    leftW: 300,
  }
  componentDidMount(){
    this._isMounted = true
  }
  industryValueChange = (value)=>{
    if(this._isMounted){
      console.log(value)
      this.setState({ industryValue:value });
    }
  }
  cityValueChange = (value)=>{
    if(this._isMounted){
      console.log(value)
      this.setState({
        cityValue: value
      })
    }
  }

  render(){
    //window动态高度
    //数选择框的属性
    const tProps = {
      treeData:this.state.treeData,
      value: this.state.industryValue,
      onChange: this.industryValueChange,
      treeCheckable: true,
      showCheckedStrategy: SHOW_PARENT,
      searchPlaceholder: '默认全部产业',
      multiple: true,
      allowClear: true,
      treeNodeFilterProp: 'title',
      maxTagCount: 1,
      maxTagPlaceholder:'...共选'+this.state.industryValue.length+'项',
      style: {
        minWidth: 200,
      },
    }
    const relH = this.props.windowH - 100
    return (
      <div className="formap" css={{position: "relative"}}>
          <div css={{display:"flex"}}>
            <div css={{flex:`0 0 ${this.state.leftW}px`}}>left</div>
            <div css={{height: `${relH}px`,flex:"1 1 auto"}}>
              <RbMap />
            </div>
          </div>

        <div css={{width:"100%",height:"50px",lineHeight:"50px",position: "absolute",background:"#ffffff",top:"0px",padding: "0 20px 0 20px",background:"rgba(25,33,57,0.8)"}}>
          <Search
            placeholder="输入产业园查找"
            onSearch={value => console.log(value)}
            style={{ width: 200 }}
          />
          <TreeSelect {...tProps} />
          <TreeSelect
            style={{ width: 300 }}
            value={this.state.cityValue}
            dropdownStyle={{ maxHeight: 400, overflow: 'auto' }}
            treeData={this.state.cityData}
            placeholder="默认为全国"
            treeDefaultExpandAll
            treeNodeFilterProp='title'
            showSearch={true}
            onChange={this.cityValueChange}
            maxTagCount={0}
            maxTagPlaceholder={this.state.cityValue.length}
          />


        </div>
        <span css={{width:"40px",height:"40px",borderRadius: "40px",background:"rgba(255,255,255,0.5)", position:"absolute", top:"60px",cursor:"pointer"}}>
          <i className="fa fa-arrow-left" aria-hidden="true"></i>
        </span>
      </div>
    )
  }
}
const mapStateToProps = (state)=>({
  windowH: state.windowH.windowH
})

export default connect(mapStateToProps,null)(WholeView)
